
public class seriesprog1 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int c=1;
		for (int i=1;i<=4;i++){
	    	   for(int j=1;j<i+1;j++)
	    	   {    
	    		   System.out.print(c+"\t");
	    		   c++;
	    	   }
	    	   System.out.println("");
	    	   
	       }

	}

}
