
public class starprog3 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i=1;i<=4;i++){
			for(int k=4;k>=i;k--)
			{
				System.out.print("\t");
			}
	    	   for(int j=1;j<=i;j++)
	    		   //
	    	   {
	    		   System.out.print("\t"+"*"+"\t");
	    	   }
	    	   System.out.println("");
	    	   System.out.println("");
	       }

	}

}
